import React, { Component } from 'react';
import Styled from 'styled-components'
import Carousel from 'react-elastic-carousel';
import logos1 from './logos/transfotech.png'
import logos2 from './logos/dnd.jpg'
import logos3 from './logos/softgudam.png'
import logos4 from './logos/transfotech.png'
import logos5 from './logos/transfotech.png'


class Client extends Component {
  state = {
    items: [
      {id: 1, title: logos1},
      {id: 2, title: logos2},
      {id: 3, title: logos3},
      {id: 4, title: logos},
      {id: 5, title: 'https://mharif.com/blog/wp-content/uploads/2020/12/dnd.jpg'},
      {id: 6, title: 'https://mharif.com/blog/wp-content/uploads/2020/12/LOGO-web.png'},
      {id: 7, title: 'https://mharif.com/blog/wp-content/uploads/2020/12/netcom.png'},
      {id: 8, title: 'https://mharif.com/blog/wp-content/uploads/2020/12/bsidyes.jpg'},
      {id: 9, title: 'item #9'},
    ]
  }

  render () {
    const { items } = this.state;
    const breakPoints = [
      { width: 320, itemsToShow: 1 },
      { width: 480, itemsToShow: 2, itemsToScroll: 2 },
      { width: 768, itemsToShow: 4 },
      { width: 450, itemsToShow: 5 }
    ];
    return (
      <Carousel breakPoints={breakPoints} >
        {items.map(item => <div className='mh-clients' key={item.id}><img src={item.title}></img></div>)}
      </Carousel>
    )
  }
}
export default Client
